mapper.update:
update user_account_biz
set is_deleted = 1,
    modifier   = #{modifier},
    cst_modify = #{cstModify}
where is_deleted = 0
  and code = #{code}

mapper.insert:
insert into user_account_biz
( code, tenant_code
, ua_code, biz_code, nickname
, portrait, user_detail, status
, cst_activate, is_deleted, creator
, cst_create, modifier, cst_modify)
values ( #{code,jdbcType=VARCHAR}, #{tenantCode,jdbcType=VARCHAR}
       , #{uaCode,jdbcType=VARCHAR}, #{bizCode,jdbcType=VARCHAR}, #{nickname,jdbcType=VARCHAR}
       , #{portrait,jdbcType=VARCHAR}, #{userDetail,jdbcType=VARCHAR}, #{status,jdbcType=TINYINT}
       , #{cstActivate,jdbcType=TIMESTAMP}, #{isDeleted,jdbcType=TINYINT}, #{creator,jdbcType=VARCHAR}
       , #{cstCreate,jdbcType=TIMESTAMP}, #{modifier,jdbcType=VARCHAR}, #{cstModify,jdbcType=TIMESTAMP})
