mapper.update:
update user_account_logout
set is_deleted = 1,
    modifier   = #{modifier},
    cst_modify = #{cstModify}
where is_deleted = 0
  and code = #{code}

mapper.insert:
insert into user_account_logout
( code, tenant_code
, ua_code, biz_code, token
, cst_expire, is_deleted, creator
, cst_create, modifier, cst_modify)
values ( #{code,jdbcType=VARCHAR}, #{tenantCode,jdbcType=VARCHAR}
       , #{uaCode,jdbcType=VARCHAR}, #{bizCode,jdbcType=VARCHAR}, #{token,jdbcType=VARCHAR}
       , #{cstExpire,jdbcType=TIMESTAMP}, #{isDeleted,jdbcType=TINYINT}, #{creator,jdbcType=VARCHAR}
       , #{cstCreate,jdbcType=TIMESTAMP}, #{modifier,jdbcType=VARCHAR}, #{cstModify,jdbcType=TIMESTAMP})
