mapper.update:
update user_account
set is_deleted = 1,
    modifier   = #{modifier},
    cst_modify = #{cstModify}
where is_deleted = 0
  and code = #{code}

mapper.insert:
insert into user_account
( code, tenant_code
, username, real_name, nickname
, password, salt, email
, mobile, gender, birthday
, portrait, status, cst_activate
, is_deleted, creator, cst_create
, modifier, cst_modify)
values ( #{code,jdbcType=VARCHAR}, #{tenantCode,jdbcType=VARCHAR}
       , #{username,jdbcType=VARCHAR}, #{realName,jdbcType=VARCHAR}, #{nickname,jdbcType=VARCHAR}
       , #{password,jdbcType=VARCHAR}, #{salt,jdbcType=VARCHAR}, #{email,jdbcType=VARCHAR}
       , #{mobile,jdbcType=VARCHAR}, #{gender,jdbcType=TINYINT}, #{birthday,jdbcType=DATE}
       , #{portrait,jdbcType=VARCHAR}, #{status,jdbcType=TINYINT}, #{cstActivate,jdbcType=TIMESTAMP}
       , #{isDeleted,jdbcType=TINYINT}, #{creator,jdbcType=VARCHAR}, #{cstCreate,jdbcType=TIMESTAMP}
       , #{modifier,jdbcType=VARCHAR}, #{cstModify,jdbcType=TIMESTAMP})
