mapper.update:
update user_notice_verification
set is_deleted = 1,
    modifier   = #{modifier},
    cst_modify = #{cstModify}
where is_deleted = 0
  and code = #{code}

mapper.insert:
insert into user_notice_verification
( id, code, tenant_code
, biz_code, function_code, touch_means
, user_identity, request_code, verification
, cst_expire, is_deleted, creator
, cst_create, modifier, cst_modify)
values ( #{id,jdbcType=BIGINT}, #{code,jdbcType=VARCHAR}, #{tenantCode,jdbcType=VARCHAR}
       , #{bizCode,jdbcType=VARCHAR}, #{functionCode,jdbcType=VARCHAR}, #{touchMeans,jdbcType=VARCHAR}
       , #{userIdentity,jdbcType=VARCHAR}, #{requestCode,jdbcType=VARCHAR}, #{verification,jdbcType=VARCHAR}
       , #{cstExpire,jdbcType=TIMESTAMP}, #{isDeleted,jdbcType=TINYINT}, #{creator,jdbcType=VARCHAR}
       , #{cstCreate,jdbcType=TIMESTAMP}, #{modifier,jdbcType=VARCHAR}, #{cstModify,jdbcType=TIMESTAMP})
